import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { GeneralService } from 'src/app/services/general/general.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  wordDesForm =new FormGroup({
    word: new FormControl(""),
    description: new FormControl("")
  })
  wordInfo:any= []
  constructor(private genService:GeneralService, private route: Router){
    this.getWordInfo();
  }

  wordSubmit(){

    this.genService.sendWordData(this.wordDesForm.value).subscribe(
      result=>{
        this.route.navigate(['home/share/word'])
      }
    )

  }

  getWordInfo(){
    this.genService.getWordInfo().subscribe(result=>{
      console.log("1233here",result)
      this.wordInfo = result;
    })
  }
}
