import { Component } from '@angular/core';
import { FormControl, FormGroup, NgControl } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth/auth.service';
import { GeneralService } from 'src/app/services/general/general.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  loginForm = new FormGroup(
    {
      username: new FormControl(''),
      password: new FormControl('')
    }
  )

  constructor(private loginService:AuthService, private route: Router){}

  loginFormSubmit(){
    console.log(this.loginForm)
    this.loginService.login(this.loginForm.value.username, this.loginForm.value.password).subscribe(result=>{
      console.log('here', result)
      this.route.navigate(['/home/share/word'])
    })
  }
}
<div class="container">
    <div class="row">
        <div class="col-4">

        </div>
        <div class="col-4">
            <form class="form form-group" [formGroup]="loginForm" (submit)="loginFormSubmit()">
                <input class="form-control form-control-lg" placeholder="username" type="text" formControlName="username"/>
                <input class="form-control form-control-lg" placeholder="password" type="password" formControlName="password"/>
                <button type="submit" class="btn btn-primary mt-3" (click)="loginFormSubmit()">Login</button>
            </form>
        </div>
        <div class="col-4">

        </div>

    </div>
</div>
